import './App.css';
// import { Component } from 'react';
// import { connect } from 'react-redux';
import {useDipatch ,useDispatch,useSelector} from 'react-redux'
import axios from 'axios';

import {addUser} from './redusers/user/user'

//import for counter 

//for action
import { addTodos, addTodo, removeTodo } from './redusers/todos/todos';
import { useEffect ,useState } from 'react';


export default function App (){
   const [todo ,setTodo]=useState()
   const[user ,setUser]=useState();
   const dispatch =useDispatch()

const state=useSelector((state)=>{


  return{
    todos:state.todos.todos,
    user:state.user.user
  }
})

// send dispatch

const addTodo=()=>{
  const todo={}
  dispatch(addTodo(todo))
}

useEffect(()=>{
  axios.get('https://jsonplaceholder.typicode.com/todos/')
  .then((res)=>{
    dispatch(addTodo(res.data))
  })
  .catch((err)=>{
    console.log(err)
  })
},[])
  return (
     <>
     <h2>Lab 2</h2>
     <h2>Use Redux with Axios </h2>

      {state.todos.map((elm)=>{

        return(
          <>
         <p>{elm.id} 
         <br>
           {elm.title.toUpperCase()}
         </br></p>  
         <button onClick={()=>dispatch(removeTodo(elm))}> Remove8\</button>
          </>
        )
      })}
     </>
  );
}
